sys_script_include.script:
var RegisteredStudentUtils = Class.create();
RegisteredStudentUtils.prototype = {
    updateState: function(sysId, value) {
        var gr = new GlideRecord('x_1014103_studen_0_registered_students');
        gr.addQuery('sys_id', sysId);
        gr.query();

        if (gr.next()) {
            gr.setValue('state', value); 
            gr.update();
			return true;
        } else {
			gs.info('error');
			return false;
         }

    },

    type: 'RegisteredStudentUtils'
};
