var UpdateRegisteredStudentsState = Class.create();
UpdateRegisteredStudentsState.prototype = Object.extendsObject(AbstractAjaxProcessor, {
    updateState: function() {
        var gr = new GlideRecord('x_1014103_studen_0_registered_students');
        var sysId = this.getParameter('sysparm_id');

        if (gr.get(sysId)) {
            gr.setValue('state', 2);
            gr.update();
            return true;
        } else {
            gs.info("Record not found.");
            return false;
        }
    },

    type: 'UpdateRegisteredStudentsState'
});
